import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const RideOffers = () => {
  //const [rideOffers, setRideOffers] = useState([]);

    let rideOffers;

    const fetchRideOffers = async () => {
        const token = localStorage.getItem('token');
        try {
          const response = await fetch('http://localhost:5000/api/ride-offers', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          });
          rideOffers = await response.json();
          console.log(rideOffers[0]);
          if(!response.ok)
              {
                  console.log('Error');
              }
        } catch (error) {
          console.log(error.message)
      }
      };
      fetchRideOffers();
      
  if (!rideOffers || rideOffers.length === 0) {
    return <div>No ride offers available</div>;
  }

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Ride Offers</h2>
      <ul>
        { rideOffers.map( (item,index) => {
            <li key={item[index].id}>{item[index]['pickup_location']}</li>
        })}
      </ul>
    </div>
  );
};

export default RideOffers;
