import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RideMatches = ({ matches, handleBookRide }) => {
  return (
    <div>
      {matches.map((match) => (
        <div key={match.id}>
          <div>Ride ID: {match.id}</div>
          <button onClick={() => handleBookRide(match.id)}>Book Ride</button>
        </div>
      ))}
    </div>
  );
};

const RideMatchingPage = () => {
  const [matches, setMatches] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchMatches = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/rides');
        setMatches(response.data);
      } catch (error) {
        setError('Failed to fetch ride matches');
      }
    };

    fetchMatches();
  }, []);

  const handleBookRide = async (rideId) => {
    try {
      await axios.post(`http://localhost:5000/api/book/${rideId}`);
      alert('Ride booked successfully');
    } catch (error) {
      alert('Failed to book ride');
    }
  };

  return (
    <div>
      {error && <div>{error}</div>}
      <RideMatches matches={matches} handleBookRide={handleBookRide} />
    </div>
  );
};

export default RideMatchingPage;
