import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const RideOffers = ({ rideOffers }) => {
  if (!rideOffers || rideOffers.length === 0) {
    return <div>No ride offers available</div>;
  }

  return (
    <div>
      <h2>Ride Offers</h2>
      <MapContainer center={[12.9716, 77.5946]} zoom={10} style={{ height: '400px' }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {rideOffers.map(offer => (
          <Marker position={[offer.latitude, offer.longitude]} key={offer.id}>
            <Popup>
              <div>
                <h3>{offer.pickup_location}</h3>
                <p>Destination: {offer.destination}</p>
                <p>Date/Time: {offer.date_time}</p>
                <p>Seats: {offer.seats}</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default RideOffers;
