import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const RideOffers = () => {
  //const [rideOffers, setRideOffers] = useState([]);

    let rideOffers;

    const fetchRideOffers = async () => {
        const token = localStorage.getItem('token');
        try {
          const response = await fetch('http://localhost:5000/api/ride-offers', {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json',
            },
          });
          rideOffers = await response.json();
          console.log(rideOffers);
          if(!response.ok)
              {
                  console.log('Error');
              }
        } catch (error) {
          console.log(error.message)
      }
      };

  if (!rideOffers || rideOffers.length === 0) {
    return <div>No ride offers available</div>;
  }

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">Ride Offers</h2>
      <MapContainer center={[12.9716, 77.5946]} zoom={10} style={{ height: '400px' }}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {rideOffers.map(offer => (
          <Marker position={[offer.latitude, offer.longitude]} key={offer.id}>
            <Popup>
              <div>
                <h3 className="font-semibold">{offer.pickup_location}</h3>
                <p>Destination: {offer.destination}</p>
                <p>Date/Time: {offer.date_time}</p>
                <p>Seats: {offer.seats}</p>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
};

export default RideOffers;
