import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RideOffers = () => {
  const [rideOffers, setRideOffers] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRideOffers = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get('http://localhost:5000/api/ride-offers', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setRideOffers(response.data);
      } catch (error) {
        console.error('Error fetching ride offers:', error);
        setError('Failed to fetch ride offers');
      }
    };

    fetchRideOffers();
  }, []);

  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md max-w-md w-full">
        <h2 className="text-2xl text-center mb-6">Available Rides</h2>
        {error && <div className="text-red-500 mb-4">{error}</div>}
        {rideOffers.length === 0 && !error && <div className="text-gray-700">No available rides</div>}
        {rideOffers.map((rideOffer) => (
          <div key={rideOffer.id} className="border p-4 mb-4 rounded">
            <div className="flex justify-between mb-2">
              <div><strong>Pickup Location:</strong> {rideOffer.pickup_location}</div>
              <div><strong>Destination:</strong> {rideOffer.destination}</div>
            </div>
            <div className="flex justify-between">
              <div><strong>Date & Time:</strong> {rideOffer.date_time}</div>
              <div><strong>Available Seats:</strong> {rideOffer.available_seats}</div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RideOffers;
