import React, { useState } from 'react';

const Booking = () => {
  const [rideOfferId, setRideOfferId] = useState('');
  const [seatsBooked, setSeatsBooked] = useState('');
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/book-ride', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rideOfferId, seatsBooked }),
      });

      if (response.ok) {
        setSuccessMessage('Ride booked successfully!');
      } else {
        const errorText = await response.text();
        setError(errorText);
      }
    } catch (error) {
      console.error('Error:', error);
      setError('An error occurred');
    }
  };

  return (
    <div>
      <h2>Book a Ride</h2>
      {error && <p>Error: {error}</p>}
      {successMessage && <p>{successMessage}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="rideOfferId">Ride Offer ID:</label>
          <input
            type="text"
            id="rideOfferId"
            value={rideOfferId}
            onChange={(e) => setRideOfferId(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="seatsBooked">Seats Booked:</label>
          <input
            type="number"
            id="seatsBooked"
            value={seatsBooked}
            onChange={(e) => setSeatsBooked(e.target.value)}
            required
          />
        </div>
        <button type="submit">Book Ride</button>
      </form>
    </div>
  );
};

export default Booking;
