import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios for making API requests
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'; // Import necessary components from react-leaflet

const RideOffers = () => {
  const [rideOffers, setRideOffers] = useState([]);
  const [map, setMap] = useState(null); // State to store the map instance
  const bangaloreCoords = [12.9716, 77.5946]; // Coordinates of Bangalore

  useEffect(() => {
    // Make a request to the backend API endpoint
    axios
      .get('/api/ride-offers', {
        headers: {
          'Content-Type': 'application/json',
          // Include the JWT token for authentication if needed
          // Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setRideOffers(response.data);
      })
      .catch((error) => {
        console.error('Error fetching ride offers:', error);
      });
  }, []);

  const handleMapReady = (map) => {
    setMap(map); // Store the map instance in the state
  };

  return (
    <div className="mt-4">
      <h2 className="text-lg font-bold mb-2">Ride Offers</h2>
      <div className="space-y-2">
        {rideOffers.map((offer) => (
          <div key={offer.id} className="border p-4 rounded-lg shadow-md">
            <p>
              <span className="font-bold">Pickup Location:</span> {offer.pickup_location}
            </p>
            <p>
              <span className="font-bold">Destination:</span> {offer.destination}
            </p>
            <p>
              <span className="font-bold">Date/Time:</span> {offer.date_time}
            </p>
            <p>
              <span className="font-bold">Seats:</span> {offer.seats}
            </p>
          </div>
        ))}
      </div>
      <div className="mt-4">
        <MapContainer
          center={bangaloreCoords}
          zoom={12}
          scrollWheelZoom={true}
          whenReady={handleMapReady}
          style={{ height: '400px', width: '100%' }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {rideOffers.map((offer) => (
            <Marker
              key={offer.id}
              position={[offer.pickup_latitude, offer.pickup_longitude]}
            >
              <Popup>
                <div>
                  <p>
                    <span className="font-bold">Pickup Location:</span> {offer.pickup_location}
                  </p>
                  <p>
                    <span className="font-bold">Destination:</span> {offer.destination}
                  </p>
                  <p>
                    <span className="font-bold">Date/Time:</span> {offer.date_time}
                  </p>
                  <p>
                    <span className="font-bold">Seats:</span> {offer.seats}
                  </p>
                </div>
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>
    </div>
  );
};

export default RideOffers;
