import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for marker icon not displaying properly
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon-2x.png',
  iconUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-icon.png',
  shadowUrl: 'https://cdnjs.cloudflare.com/ajax/libs/leaflet/1.7.1/images/marker-shadow.png',
});

// Custom hook to re-center the map
const RecenterMap = ({ center }) => {
  const map = useMap();
  useEffect(() => {
    map.setView(center);
  }, [center, map]);
  return null;
};

const RideOffers = () => {
  const [rideOffers, setRideOffers] = useState([]);
  const [coordinates, setCoordinates] = useState({});
  const [userLocation, setUserLocation] = useState(null); // Initial state set to null
  const graphhopperApiKey = '7ec57cd6-a8bb-4c68-98c2-61d051f28e6e'; // Replace with your GraphHopper API key

  useEffect(() => {
    // Get the user's location
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          setUserLocation([position.coords.latitude, position.coords.longitude]);
        },
        (error) => {
          console.error('Error fetching user location:', error);
          setUserLocation([12.9716, 77.5946]); // Default to Bangalore coordinates if user denies permission or an error occurs
        }
      );
    } else {
      setUserLocation([12.9716, 77.5946]); // Default to Bangalore coordinates if geolocation is not available
    }

    axios
      .get('/api/ride-offers', {
        headers: {
          'Content-Type': 'application/json',
          // Include the JWT token for authentication if needed
          // Authorization: `Bearer ${token}`,
        },
      })
      .then((response) => {
        setRideOffers(response.data);
      })
      .catch((error) => {
        console.error('Error fetching ride offers:', error);
      });
  }, []);

  useEffect(() => {
    const fetchCoordinates = async (location) => {
      try {
        const response = await axios.get(`https://graphhopper.com/api/1/geocode?q=${location}&key=${graphhopperApiKey}`);
        const { lat, lng } = response.data.hits[0].point;
        return { lat, lng };
      } catch (error) {
        console.error('Error fetching coordinates:', error);
        return null;
      }
    };

    const updateCoordinates = async () => {
      const coords = {};
      for (const offer of rideOffers) {
        if (!coordinates[offer.pickup_location]) {
          coords[offer.pickup_location] = await fetchCoordinates(offer.pickup_location);
        }
        if (!coordinates[offer.destination]) {
          coords[offer.destination] = await fetchCoordinates(offer.destination);
        }
      }
      setCoordinates((prev) => ({ ...prev, ...coords }));
    };

    if (rideOffers.length > 0) {
      updateCoordinates();
    }
  }, [rideOffers, coordinates]);

  return (
    <div className="container mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Ride Offers</h2>
      {userLocation && (
        <MapContainer center={userLocation} zoom={10} style={{ height: '400px' }}>
          <RecenterMap center={userLocation} />
          <TileLayer
            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          <Marker position={userLocation}>
            <Popup>Your Location</Popup>
          </Marker>
          {rideOffers.map((offer) => {
            const pickupCoords = coordinates[offer.pickup_location];
            const destinationCoords = coordinates[offer.destination];
            return pickupCoords && destinationCoords ? (
              <Marker position={[pickupCoords.lat, pickupCoords.lng]} key={offer.id}>
                <Popup>
                  <div>
                    <h3 className="font-bold">{offer.pickup_location}</h3>
                    <p>Destination: {offer.destination}</p>
                    <p>Date/Time: {offer.date_time}</p>
                    <p>Seats: {offer.seats}</p>
                  </div>
                </Popup>
              </Marker>
            ) : null;
          })}
        </MapContainer>
      )}
      <div className="mt-4">
        {rideOffers.map((offer) => (
          <div key={offer.id} className="border p-4 mb-2 rounded">
            <h3 className="font-bold">{offer.pickup_location}</h3>
            <p>Destination: {offer.destination}</p>
            <p>Date/Time: {offer.date_time}</p>
            <p>Seats: {offer.seats}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RideOffers;
